export const getShapeLabel = (id: string, fallback?: string) => {
  const raw = SHAPE_LABELS[id];
  if (raw) return raw;
  if (fallback) return fallback;
  const pretty = id.replace(/[-_]/g, ' ').trim();
  return pretty.charAt(0).toUpperCase() + pretty.slice(1);
};
